package cn.bcd.parser.protocol.gb32960;

import cn.bcd.parser.base.util.PerformanceUtil;
import cn.bcd.parser.protocol.gb32960.data.Packet;
import io.netty.buffer.ByteBuf;
import io.netty.buffer.ByteBufUtil;
import io.netty.buffer.Unpooled;
import org.junit.jupiter.api.Test;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public class Parser {
    static Logger logger = LoggerFactory.getLogger(Parser.class);


    @Test
    public void test() {
        cn.bcd.parser.base.Parser.withDefaultLogCollector_parse();
        cn.bcd.parser.base.Parser.withDefaultLogCollector_deParse();
        cn.bcd.parser.base.Parser.enableGenerateClassFile();
        cn.bcd.parser.base.Parser.enablePrintBuildLog();
//        String data = "";
//        String data = "";
//        String data = "232305013230303030304c534a535130303030303001000617060a09262042";
//        String data = "23 23 03 01 4C 53 4A 41 32 34 30 33 30 48 53 31 39 32 39 36 39 01 00 00 FE";
//        String data = "232301fe4c534a4533363039364d5331343034393501001e170b01110f1900013030303030303030303030303030303030303030000000";
        String data = "";
//        String data = "232304fe4c534a4533363039364d53313430343935010008170b01100f1e000100";
        data = data.replaceAll(" ", "");
        byte[] bytes = ByteBufUtil.decodeHexDump(data);
        ByteBuf byteBuf = Unpooled.wrappedBuffer(bytes);
        Packet packet = Packet.read(byteBuf);
        ByteBuf dest = Unpooled.buffer();
        packet.write(dest);
        logger.info(data.toUpperCase());
        logger.info(ByteBufUtil.hexDump(dest).toUpperCase());
        assert data.equalsIgnoreCase(ByteBufUtil.hexDump(dest));
    }

    @Test
    public void test_performance() {
        cn.bcd.parser.base.Parser.enablePrintBuildLog();
        cn.bcd.parser.base.Parser.enableGenerateClassFile();
        String data = "";
//        String data = "232304fe4c534a4533363039364d53313430343935010008170b01100f1e000100";
        int threadNum = 1;
        logger.info("param threadNum[{}]", threadNum);
        int num = 2000000000;
        PerformanceUtil.testPerformance(ByteBufUtil.decodeHexDump(data), threadNum, num, Packet::read, (buf, instance) -> instance.write(buf), true);
    }
}
